import { View, Text,Image, TouchableOpacity } from 'react-native'
import React from 'react'
import {MaterialCommunityIcons} from 'react-native-vector-icons'

const RestaurantItems = ({restaurants}) => {

    const RestaurantImage = (props) => (
        <>
            <Image 
                source = {{url: props.image_url}}
                style ={{width: "100%", height: 180}}
            />
            <TouchableOpacity style={{position: "absolute", right:20}}>
                <MaterialCommunityIcons name ="heart-outline" size = {25} color = "white"/>
            </TouchableOpacity>
        </>

    )

    const RestaurantInfo = (props) => (
        <View style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            marginTop: 10,
        }}>
            <View>
                <Text style={{fontSize:15, fontWeight: "bold"}}>{props.name}</Text>
                <Text style={{fontSize:13, color: 'green'}}>30-45 â€¢ min </Text>
            </View>
                <View
                    style={{
                        backgroundColor: '#eee', 
                        height: 30,
                        width:30,
                        alignItems: 'center',
                        borderRadius: 50,
                        justifyContent: 'center'
                    }}
                >
                   <Text>{props.rating}</Text> 
                </View>

        </View>
    )


  return (
        <TouchableOpacity activeOpacity={1 } style={{marginBottom: 30}}>
            {restaurants.map((restaurant, id) => 
                <View key = {id} style={{marginTop:10, padding: 15, backgroundColor:'white'}}>
                <RestaurantImage image= {restauraunt.image}/>
                <RestaurantInfo name= {restaurant.name} rating = {restaurant.rating}/>
            </View> 
            )}

        </TouchableOpacity>

  )
}

export default RestaurantItems
