import { View, Text, TouchableOpacity } from 'react-native'
import {React, useState} from 'react'

const HeaderTabs = () => {
    const [toggle, setToggle] = useState("Delivery")

    // Think of button subcomponent, destructuring and passing props 
    // using useState to set a toggle 
    const HeaderButton = (props) => (
        <View >
            {/* Thouchability allows you to add onclick functionality  */}
            <TouchableOpacity
                style={{ 
                    backgroundColor: props.toggle === props.text ? 'black' : 'white',
                    paddingVertical: 5,
                    paddingHorizontal: 20,
                    borderRadius:40
                }}
                onPress ={()=> props.setToggle(props.text) }
            >
                <Text style={{color: props.toggle === props.text ? "white" : 'black', fontSize: 15, fontWeight: "900"}}>{props.text}</Text>
            </TouchableOpacity>
        </View>
    )

  return (
    <View style={{flexDirection: 'row', alignSelf: 'center'}}>
      <HeaderButton text ="Delivery"  toggle = {toggle} setToggle = {setToggle}/>
      <HeaderButton text = "Pickup"  toggle = {toggle} setToggle = {setToggle}/>
    </View>
  )
}

export default HeaderTabs
