// use rnf to create a react native function 
import { SafeAreaView, Text, View, ScrollView } from 'react-native'
import HeaderTabs from '../components/HeaderTabs'
import Searchbar from '../components/Searchbar'
import Categories from '../components/Categories'
import RestaurantItems from '../components/RestaurantItems'
import { React, useEffect, useState } from 'react'

export default function Home() {
  const [restaurants, setRestaurants] = useState(restaurants)

  const YELP_API_KEY = 
  'XOfbkvzZ03OsqOPA4An2yO5dJAGxm0fxhwPtISbWeZcz0VP_iqyFSivGDkFAJbfo2DDPXh7Gd-BISbMKwxO8jvQuqDcX8V2gKPLZsYX4SCungVgigQLkCmrffaJBYnYx'
  const apiOptions = {
    headers:{
      Authorization: `Bearer ${YELP_API_KEY}`
    }
  }

  useEffect(() => {
    fetch(`https://api.yelp.com/v3/businesses/search?term=restaurants&location=SanDiego`, apiOptions)
    .then(response => response.json())
    .then(restaurantsData => setRestaurants(restaurantsData.businesses))
    console.log(restaurantsData)
  }, [])





  return (
    // SafeAreaView is used for viewing in the proper space, 
    <SafeAreaView style={{backgroundColor: '#eee', flex: 1, }}>
        <View style={{backgroundColor: 'white', padding: 15}}>
          <HeaderTabs/>
          <Searchbar/>
        </View>
        <ScrollView>
          <Categories/>
          <RestaurantItems restaurants={restaurants}/>
        </ScrollView>
    </SafeAreaView>
  )
}
